import React, { useEffect, useRef, useState } from 'react';
import './App.css';

const questions = [
  { q: "What's our first date memory?", options: ["Beach walk", "Movie night", "Coffee shop"], correct: 0 },
  { q: "Favorite trip together?", options: ["Goa", "Mountains", "Home stay"], correct: 1 },
  { q: "Song that reminds us of us?", options: ["Perfect", "Shape of You", "Our song"], correct: 0 }
];

const gifs = {
  initial: 'https://media.giphy.com/media/Ju7l5y9osyymQ/giphy.gif',  // Cute couple
  no1: 'https://media.giphy.com/media/3o7aDgf1211jMJmMT2/giphy.gif',     // Sad puppy
  no2: 'https://media.giphy.com/media/26ufnwz3wDUli7GU0/giphy.gif',     // Heart eyes
  yes: 'https://media.giphy.com/media/l0HlRnAWXxn0MhKLK/giphy.gif',      // Celebration
  quiz: 'https://media.giphy.com/media/3o7btPCcdNniyf0ArS/giphy.gif',    // Love hearts
  gift: 'https://media.giphy.com/media/26ufnR4Bsha9J2jWi/giphy.gif'       // Gift opening
};

function App() {
  const [step, setStep] = useState('valentine');
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState([]);
  const [score, setScore] = useState(0);
  const [noClicks, setNoClicks] = useState(0);
  const [showLetter, setShowLetter] = useState(false);
  const canvasRef = useRef(null);
  const animationRef = useRef(null);

  // Confetti (canvas-confetti style)
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    let confetti = [];

    const resizeCanvas = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    const initConfettiBurst = () => {
      for (let i = 0; i < 200; i++) {
        confetti.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height - canvas.height,
          vx: (Math.random() - 0.5) * 10,
          vy: Math.random() * 3 + 2,
          color: `hsl(${Math.random() * 60 + 300}, 70%, 60%)`,
          size: Math.random() * 5 + 3,
          rotation: Math.random() * 360,
          rotSpeed: (Math.random() - 0.5) * 10
        });
      }
    };

    const renderConfetti = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      confetti = confetti.filter(c => {
        c.x += c.vx;
        c.y += c.vy;
        c.vy += 0.1;
        c.rotation += c.rotSpeed;

        ctx.save();
        ctx.translate(c.x, c.y);
        ctx.rotate(c.rotation * Math.PI / 180);
        ctx.fillStyle = c.color;
        ctx.fillRect(-c.size/2, -c.size/2, c.size, c.size);
        ctx.restore();

        return c.y < canvas.height && c.x > -50 && c.x < canvas.width + 50;
      });

      if (confetti.length > 0) {
        animationRef.current = requestAnimationFrame(renderConfetti);
      }
    };

    resizeCanvas();
    window.addEventListener('resize', resizeCanvas);

    return () => {
      window.removeEventListener('resize', resizeCanvas);
      if (animationRef.current) cancelAnimationFrame(animationRef.current);
    };
  }, []);

  const triggerConfetti = () => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      // Restart confetti animation
      const script = document.createElement('script');
      script.innerHTML = `
        (function() {
          const confetti = [];
          const canvas = document.querySelector('.confetti-canvas');
          const ctx = canvas.getContext('2d');
          function animate() {
            ctx.clearRect(0, 0, canvas.width, canvas.height);
            // ... confetti logic
            requestAnimationFrame(animate);
          }
        })();
      `;
      document.head.appendChild(script);
    }
  };

  const noMessages = ['Are you sure? ü•∫', 'Think again please! üíï', 'I know you want to say Yes! üòò'];

  const handleYes = () => {
    setStep('quiz');
    triggerConfetti();
  };

  const handleNo = () => {
    setNoClicks(prev => Math.min(prev + 1, noMessages.length - 1));
  };

  const selectAnswer = (optIndex) => {
    const newAnswers = [...answers];
    newAnswers[currentQuestion] = optIndex;
    setAnswers(newAnswers);
  };

  const nextQuestion = () => {
    let newScore = score;
    if (answers[currentQuestion] === questions[currentQuestion].correct) newScore++;
    setScore(newScore);

    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      setStep('gift');
      triggerConfetti();
    }
  };

  const openLetter = () => {
    setShowLetter(true);
    triggerConfetti();
  };

  const restart = () => {
    setStep('valentine');
    setCurrentQuestion(0);
    setAnswers([]);
    setScore(0);
    setNoClicks(0);
    setShowLetter(false);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-100 via-rose-100 to-purple-100 flex flex-col items-center justify-center p-8 relative overflow-hidden">
      <canvas ref={canvasRef} className="confetti-canvas fixed inset-0 z-0 pointer-events-none" />

      {/* Floating hearts background */}
      <div className="fixed inset-0 overflow-hidden z-10 pointer-events-none">
        <div className="absolute w-12 h-12 bg-gradient-to-r from-pink-400 to-rose-500 rounded-full opacity-20 animate-ping" style={{animationDelay: '0s', left: '10%', top: '20%'}}></div>
        <div className="absolute w-16 h-16 bg-gradient-to-r from-purple-400 to-pink-500 rounded-full opacity-20 animate-bounce" style={{animationDelay: '2s', right: '15%', bottom: '30%'}}></div>
        <div className="absolute w-10 h-10 bg-gradient-to-r from-rose-400 to-orange-500 rounded-full opacity-20 animate-pulse" style={{animationDelay: '4s', left: '70%', top: '60%'}}></div>
      </div>

      {step === 'valentine' && (
        <div className="max-w-md w-full text-center z-20">
          <img
            src={gifs.initial}
            alt="Cute couple"
            className="w-64 h-64 mx-auto rounded-2xl shadow-2xl mb-8 animate-bounce-slow"
          />
          <h1 className="text-4xl md:text-5xl font-black bg-gradient-to-r from-pink-600 via-rose-600 to-purple-600 bg-clip-text text-transparent mb-12">
            Will you be my Valentine? üíï
          </h1>
          <div className="space-y-4">
            <button
              onClick={handleYes}
              className="w-full max-w-xs mx-auto block px-12 py-6 bg-gradient-to-r from-pink-500 to-rose-500 text-white font-bold text-xl rounded-3xl shadow-2xl hover:shadow-pink-500/50 hover:scale-105 transition-all duration-300 transform hover:-translate-y-1"
            >
              Yes ‚ù§Ô∏è
            </button>
            <button
              onClick={handleNo}
              className="w-full max-w-xs mx-auto block px-12 py-6 bg-gradient-to-r from-gray-400 to-gray-500 text-white font-bold text-xl rounded-3xl shadow-xl hover:shadow-gray-400/50 hover:scale-105 transition-all duration-300 transform hover:-translate-y-1 opacity-75"
            >
              {noMessages[noClicks] || 'No üò¢'}
            </button>
          </div>
        </div>
      )}

      {step === 'quiz' && (
        <div className="max-w-md w-full text-center z-20">
          <img src={gifs.quiz} alt="Love quiz" className="w-48 h-48 mx-auto rounded-full shadow-2xl mb-8" />
          <h2 className="text-3xl font-bold text-pink-600 mb-8">Our Love Quiz! üíï</h2>
          <div className="bg-white/80 backdrop-blur-xl rounded-3xl p-8 shadow-2xl mb-8">
            <p className="text-xl mb-6 font-semibold">{questions[currentQuestion].q}</p>
            <div className="space-y-3">
              {questions[currentQuestion].options.map((opt, idx) => (
                <button
                  key={idx}
                  onClick={() => selectAnswer(idx)}
                  disabled={answers[currentQuestion] !== undefined}
                  className={`w-full p-4 rounded-2xl font-semibold transition-all duration-300 ${
                    answers[currentQuestion] !== undefined
                      ? 'bg-gray-200 text-gray-500 cursor-not-allowed'
                      : 'bg-gradient-to-r from-pink-400 to-rose-400 hover:from-pink-500 hover:to-rose-500 text-white shadow-lg hover:shadow-pink-500/50 hover:scale-102 transform'
                  }`}
                >
                  {opt}
                </button>
              ))}
            </div>
          </div>
          <button
            className="px-12 py-4 bg-gradient-to-r from-purple-500 to-pink-500 text-white font-bold text-lg rounded-2xl shadow-xl hover:shadow-purple-500/50 hover:scale-105 transition-all duration-300"
            onClick={nextQuestion}
            disabled={answers[currentQuestion] === undefined}
          >
            Next Question ‚û°Ô∏è
          </button>
        </div>
      )}

      {step === 'gift' && (
        <div className="max-w-md w-full text-center z-20">
          <img src={gifs.gift} alt="Gift" className="w-64 h-64 mx-auto rounded-2xl shadow-2xl mb-8 animate-pulse" />
          <h2 className="text-4xl font-bold bg-gradient-to-r from-emerald-500 to-teal-500 bg-clip-text text-transparent mb-8">
            Perfect Score! üéÅ
          </h2>
          {score === questions.length && (
            <p className="text-xl text-pink-600 font-semibold mb-8">You're my soulmate üíë</p>
          )}
          <button
            className="px-16 py-6 bg-gradient-to-r from-emerald-500 to-teal-500 text-white font-bold text-xl rounded-3xl shadow-2xl hover:shadow-emerald-500/50 hover:scale-105 transition-all duration-300 mb-8"
            onClick={openLetter}
          >
            Open Your Gift üéÄ
          </button>
          {showLetter && (
            <div className="bg-gradient-to-b from-pink-50 to-rose-50 border-4 border-pink-200 rounded-3xl p-8 shadow-2xl backdrop-blur-xl">
              <h3 className="text-2xl font-bold text-pink-600 mb-6">üíå My Love Letter</h3>
              <div className="text-lg leading-relaxed text-gray-800 font-medium">
                Dear [His Name],<br/><br/>
                From our first date to every little moment since, you've made my world brighter.
                I love your laugh, your kindness, and how you make every day special.<br/><br/>
                Will you make me the happiest girl by being my Valentine?<br/><br/>
                Forever yours,<br/>
                [Your Name] üíï
              </div>
            </div>
          )}
          <button
            className="px-12 py-4 mt-8 bg-gradient-to-r from-blue-500 to-indigo-500 text-white font-bold text-lg rounded-2xl shadow-xl hover:shadow-blue-500/50 hover:scale-105 transition-all duration-300"
            onClick={restart}
          >
            Play Again? üòä
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
